(1, 'esxi', 'NOESCAPE 1\r\ndefault vesamenu.c32\r\nONTIMEOUT esxi\r\nINCLUDE menu.colors\r\n\r\n\r\nLABEL esxi\r\nMENU label ESXi %version%\r\nKERNEL boot/esxi/%version%/iso/mboot.c32\r\nAPPEND -c boot/esxi/%version%/boot.cfg ks=http://%myip%/%mac%.cfg', 1, 0, 0, 0, 0, '# ESXi Kickstart file\r\n# Accept the VMware End User License Agreement\r\nvmaccepteula\r\n# Set the root password for the DCUI and Tech Support Mode\r\nrootpw %password%\r\ninstall --firstdisk --overwritevmfs\r\n\r\nclearpart --firstdisk --overwritevmfs\r\nnetwork --bootproto=static --device=vmnic0 --ip=%ip% --netmask=%netmask% --gateway=%gateway% --nameserver=%nameserver% --hostname=%hostname%\r\n\r\n%firstboot --interpreter=busybox\r\n\r\n# Enable SSH and the ESXi Shell\r\nvim-cmd hostsvc/enable_ssh\r\nvim-cmd hostsvc/start_ssh\r\nvim-cmd hostsvc/enable_esx_shell\r\nvim-cmd hostsvc/start_esx_shell\r\n\r\n# This should shut down the server once the install is completed\r\n%post --interpreter=busybox\r\nhalt', 'default=0\r\ntimeout=5\r\nsplashimage=(nd)/codero_splash.xpm.gz\r\nhiddenmenu\r\n\r\ntitle VMWare ESXi %version%\r\n        root (nd)\r\n        kernel boot/esxi/%version%/iso/mboot.c32 -c boot/esxi/%version%/boot.cfg ks=http://%myip%/%mac%.cfg\r\n		'),
(2, 'xenserver', 'NOESCAPE 1\r\ndefault vesamenu.c32\r\nONTIMEOUT xenserver\r\nINCLUDE menu.colors\r\n\r\nlabel xenserver\r\n   menu label Xen Server %version%\r\n   kernel mboot.c32\r\n   append boot/citrix/xenserver/%version%/iso/boot/xen.gz watchdog com1=115200,8n1i console=com1,tty --- boot/citrix/xenserver/%version%/iso/boot/vmlinuz root=/dev/ram0 console=tty0 console=ttyS0,115200n8 ramdisk_size=32758 answerfile=http://%myip%/%mac%.cfg install --- boot/citrix/xenserver/%version%/iso/install.img', 0, 0, 0, 0, 1, '<?xml version="1.0"?>\r\n	<installation mode="fresh" srtype="lvm">\r\n		<primary-disk>sda</primary-disk>\r\n		<keymap>us</keymap>\r\n		<root-password>%password%</root-password>\r\n		<source type="nfs">%myip%:/tftpboot/boot/citrix/xenserver/%version%/iso/</source>\r\n		<driver-source type="nfs">%myip%:/tftpboot/boot/citrix/xenserver/drivers/3ware/</driver-source>\r\n		<driver-source type="nfs">%myip%:/tftpboot/boot/citrix/xenserver/drivers/adaptec/</driver-source>\r\n		<admin-interface name="eth0" proto="static">\r\n		  <ip>%ip%</ip>\r\n		  <subnet-mask>%netmask%</subnet-mask>\r\n		  <gateway>%gateway%</gateway>\r\n		</admin-interface>\r\n		<timezone>America/Phoenix</timezone>\r\n		<nameserver>%dns1%</nameserver>\r\n		<nameserver>%dns2%</nameserver>\r\n		<hostname>%hostname%</hostname>\r\n		<time-config-method>ntp</time-config-method>\r\n		<ntp-server>0.pool.ntp.org</ntp-server>\r\n	</installation>', ''),
(3, 'centos', 'NOESCAPE 1\r\ndefault vesamenu.c32\r\nONTIMEOUT cent\r\nINCLUDE menu.colors\r\n\r\nLABEL cent\r\n    MENU LABEL CentOS %version%\r\n    kernel boot/centos/%version%/vmlinuz\r\n    append ksdevice=link load_ramdisk=1 initrd=boot/centos/%version%/initrd.img network ks=http://%myip%/%mac%.cfg %vnc%\r\n', 1, 0, 0, 0, 0, '#Updated 11/04/2013 -- chrisn\r\n#Added htop to post install\r\n\r\n# Firewall configuration\r\nfirewall --disabled\r\n# Install OS instead of upgrade\r\ninstall\r\n# Use http installation media (nfs for installing from ISO file)\r\nnfs --server=%myip% --dir=/tftpboot/boot/centos/%version%\r\n#url --url http://%myip%/install/centos/%version%/\r\n# Root password\r\nrootpw %password% \r\n# System authorization information\r\nauth  --useshadow\r\n# Use text install\r\ntext\r\nfirstboot --disable\r\n# System keyboard\r\nkeyboard us\r\n# System language\r\nlang en_US\r\n# SELinux configuration\r\nselinux --disabled\r\n# Installation logging level\r\nlogging --level=info\r\n# Shutdown after installation\r\npoweroff\r\n# System timezone\r\ntimezone America/Phoenix\r\n# Network information\r\n#network --device=eth1 --bootproto=dhcp\r\n\r\n# System bootloader configuration\r\nbootloader --location=mbr --driveorder=sda\r\n\r\n# Partition clearing information\r\nzerombr\r\n#ignoredisk --only-use=sda\r\n\r\n# Disk partitioning information\r\n%diskpart%\r\n\r\n%post --log=/root/post-install-scripts.log\r\n#Run System updates\r\n/usr/bin/yum -y install yum-plugin-fastestmirror >> /root/post-install-updates.log 2>&1\r\n/usr/bin/yum -y update >> /root/post-install-updates.log 2>&1\r\n\r\n#Install ELrepo\r\nrpm --import http://elrepo.org/RPM-GPG-KEY-elrepo.org\r\nrpm -Uvh http://elrepo.org/elrepo-release-6-5.el6.elrepo.noarch.rpm\r\n\r\n#Install EPEL repo\r\nrpm -Uvh http://mirror.us.leaseweb.net/epel/6/i386/epel-release-6-8.noarch.rpm\r\n\r\n#Install unzip and htop\r\nyum install -y unzip htop\r\n\r\n#Download 3ware/Adaptec software for RAID adapters\r\nmkdir /root/3ware_tools\r\ncd /root/3ware_tools\r\nwget http://%myip%/3DM2_CLI-Linux_9.5.4_10.2.1.zip\r\ncd /root\r\nwget http://%myip%/arcconf\r\nchmod 744 /root/arcconf\r\n\r\ncat <<''EOF_DISABLE_SERVICES'' > /root/cpanel-disable-services\r\n#!/bin/bash\r\nfor service in \\\r\n   avahi-daemon \\\r\n   avahi-dnsconfd \\\r\n   bluetooth \\\r\n   capi \\\r\n   conman \\\r\n   cpus \\\r\n   irda \\\r\n   lisa \\\r\n   netplugd \\\r\n   ifplugd \\\r\n   NetworkManager \\\r\n   NetworkManagerDispatcher \\\r\n   netfs \\\r\n   netplugd \\\r\n   nfs \\\r\n   nfslock \\\r\n   nfsd \\\r\n   ncsd \\\r\n   oddjobd \\\r\n   openvpn \\\r\n   pcmcia \\\r\n   pcscd \\\r\n   portsentry \\\r\n   postfix \\\r\n   rawdevices \\\r\n   rdisc \\\r\n   rpcgssd \\\r\n   rpcidmapd \\\r\n   saslauthd \\\r\n   tcsd \\\r\n   vsftpd \\\r\n   winbind \\\r\n   wpa_supplicant \\\r\n   xfs \\\r\n   ypbind ;\r\n\r\n   do\r\n\r\n       echo "Disabling service: ${service}"\r\n       chkconfig ${service} off &>/dev/null\r\n       service ${service} stop &>/dev/null\r\ndone\r\nEOF_DISABLE_SERVICES\r\n\r\nchmod 100 /root/cpanel-disable-services\r\nsh /root/cpanel-disable-services &>/dev/null\r\nrm -f /root/cpanel-disable-services &>/dev/null\r\n\r\n/sbin/chkconfig ntpd on\r\n\r\n%controlpanel%\r\n%privatenet%\r\necho ''SUBSYSTEM=="net", ACTION=="add", DRIVERS=="?*", ATTR{address}=="%mac%", ATTR{type}=="1", KERNEL=="eth*", NAME="eth1"'' >> /etc/udev/rules.d/99-persistent-net.rules; \\\r\necho ''DEVICE=eth1'' > /etc/sysconfig/network-scripts/ifcfg-eth1; \\\r\necho ''BOOTPROTO=static'' >> /etc/sysconfig/network-scripts/ifcfg-eth1; \\\r\necho ''GATEWAY=%gateway%'' >> /etc/sysconfig/network-scripts/ifcfg-eth1; \\\r\necho ''HOSTNAME=%hostname%'' > /etc/sysconfig/network; \\\r\necho ''NETWORKING=yes'' >> /etc/sysconfig/network; \\\r\necho ''HWADDR=%mac%'' >> /etc/sysconfig/network-scripts/ifcfg-eth1; \\\r\necho ''IPADDR=%ip%'' >> /etc/sysconfig/network-scripts/ifcfg-eth1; \\\r\necho ''NETMASK=%netmask%'' >> /etc/sysconfig/network-scripts/ifcfg-eth1; \\\r\necho ''ONBOOT=yes'' >> /etc/sysconfig/network-scripts/ifcfg-eth1; \\\r\necho ''NM_CONTROLLED=no'' >> /etc/sysconfig/network-scripts/ifcfg-eth1; \\\r\necho ''%ip% %hostname%'' >> /etc/hosts; \\\r\necho ''nameserver %dns1%'' > /etc/resolv.conf; \\\r\necho ''nameserver %dns2%'' >> /etc/resolv.conf;\r\n%end\r\n\r\n%pre\r\n%pre%\r\n%end\r\n\r\n%packages\r\n@Core\r\n\r\n%end', 'default=0\r\ntimeout=5\r\nsplashimage=(nd)/codero_splash.xpm.gz\r\nhiddenmenu\r\n\r\ntitle CentOS %version%\r\n        root (nd)\r\n        kernel /boot/centos/%version%/vmlinuz ksdevice=link load_ramdisk=1 network ks=http://%myip%/%mac%.cfg\r\n        initrd /boot/centos/%version%/initrd.img\r\n		\r\n'),
