(10, 'cent5', 'NOESCAPE 1\r\ndefault vesamenu.c32\r\nONTIMEOUT cent\r\nINCLUDE menu.colors\r\n\r\nLABEL cent\r\n    MENU LABEL CentOS %version%\r\n    kernel boot/centos/%version%/vmlinuz\r\n    append ksdevice=%omac% load_ramdisk=1 initrd=boot/centos/%version%/initrd.img network ks=http://%myip%/%mac%.cfg %vnc%', 1, 0, 0, 0, 0, '# Firewall configuration\r\nfirewall --disabled\r\n# Install OS instead of upgrade\r\ninstall\r\n# Use http installation media\r\nurl --url http://%myip%/install/centos/%version%/iso/\r\n# Root password\r\nrootpw %password% \r\n# System authorization information\r\nauth  --useshadow\r\n# Use text install\r\ntext\r\nfirstboot --disable\r\n# System keyboard\r\nkeyboard us\r\n# System language\r\nlang en_US\r\n# SELinux configuration\r\nselinux --disabled\r\n# Installation logging level\r\nlogging --level=info\r\n# Shutdown after installation\r\npoweroff\r\n# System timezone\r\ntimezone America/Phoenix\r\n# Network information\r\n#network --device=eth1 --bootproto=dhcp\r\n\r\n#Load 3rd party drivers\r\n# Install Adaptec RAID controler drivers\r\ndriverdisk --source=http://%myip%/aacraid-1.2.1.30300-kernel2.6.18-371.el5-x86_64-dd.img\r\n\r\n# System bootloader configuration\r\nbootloader --location=mbr --driveorder=sda\r\n\r\n# Partition clearing information\r\nzerombr\r\n#ignoredisk --only-use=sda\r\n\r\n# Disk partitioning information\r\n%diskpart%\r\n\r\n%post --log=/root/post-install-scripts.log\r\n#Run System updates\r\n/usr/bin/yum -y install yum-plugin-fastestmirror >> /root/post-install-updates.log 2>&1\r\n/usr/bin/yum -y update >> /root/post-install-updates.log 2>&1\r\n\r\n# Install ELRepo\r\nrpm --import https://www.elrepo.org/RPM-GPG-KEY-elrepo.org\r\nrpm -Uvh http://www.elrepo.org/elrepo-release-5-4.el5.elrepo.noarch.rpm\r\n\r\n# Run system updates\r\n/usr/bin/yum -y update >> /root/post_install.log 2>&1\r\n/sbin/chkconfig --del bluetooth\r\n/sbin/chkconfig --del cups\r\n/sbin/chkconfig ntpd on\r\nyum install -y unzip\r\nmkdir /root/3ware_tools\r\ncd /root/3ware_tools\r\nwget http://%myip%/3DM2_CLI-Linux_9.5.4_10.2.1.zip\r\nwget http://%myip%/arcconf\r\nchmod 744 /root/arcconf\r\n\r\ncat <<''EOF_DISABLE_SERVICES'' > /root/cpanel-disable-services\r\n#!/bin/bash\r\nfor service in \\\r\n   avahi-daemon \\\r\n   avahi-dnsconfd \\\r\n   bluetooth \\\r\n   capi \\\r\n   conman \\\r\n   cpus \\\r\n   irda \\\r\n   lisa \\\r\n   netplugd \\\r\n   ifplugd \\\r\n   NetworkManager \\\r\n   NetworkManagerDispatcher \\\r\n   netfs \\\r\n   netplugd \\\r\n   nfs \\\r\n   nfslock \\\r\n   nfsd \\\r\n   ncsd \\\r\n   oddjobd \\\r\n   openvpn \\\r\n   pcmcia \\\r\n   pcscd \\\r\n   portsentry \\\r\n   postfix \\\r\n   rawdevices \\\r\n   rdisc \\\r\n   rpcgssd \\\r\n   rpcidmapd \\\r\n   saslauthd \\\r\n   tcsd \\\r\n   vsftpd \\\r\n   winbind \\\r\n   wpa_supplicant \\\r\n   xfs \\\r\n   ypbind ;\r\n\r\n   do\r\n\r\n       echo "Disabling service: ${service}"\r\n       chkconfig ${service} off &>/dev/null\r\n       service ${service} stop &>/dev/null\r\ndone\r\nEOF_DISABLE_SERVICES\r\n\r\nchmod 100 /root/cpanel-disable-services\r\nsh /root/cpanel-disable-services &>/dev/null\r\nrm -f /root/cpanel-disable-services &>/dev/null\r\n\r\n%controlpanel%\r\n%privatenet%\r\necho ''DEVICE=eth0'' > /etc/sysconfig/network-scripts/ifcfg-eth0; \\\r\necho ''BOOTPROTO=static'' >> /etc/sysconfig/network-scripts/ifcfg-eth0; \\\r\necho ''GATEWAY=%gateway%'' >> /etc/sysconfig/network-scripts/ifcfg-eth0; \\\r\necho ''HOSTNAME=%hostname%'' > /etc/sysconfig/network; \\\r\necho ''NETWORKING=yes'' >> /etc/sysconfig/network; \\\r\necho ''HWADDR=%mac%'' >> /etc/sysconfig/network-scripts/ifcfg-eth0; \\\r\necho ''IPADDR=%ip%'' >> /etc/sysconfig/network-scripts/ifcfg-eth0; \\\r\necho ''NETMASK=%netmask%'' >> /etc/sysconfig/network-scripts/ifcfg-eth0; \\\r\necho ''ONBOOT=yes'' >> /etc/sysconfig/network-scripts/ifcfg-eth0; \\\r\necho ''NM_CONTROLLED=no'' >> /etc/sysconfig/network-scripts/ifcfg-eth0; \\\r\necho ''%ip% %hostname%'' >> /etc/ho